import{_ as e,g as p}from"./app.276e6fe4.js";const i={},r=p('<h1 id="\u8BBE\u8BA1\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u8BBE\u8BA1\u6A21\u5F0F" aria-hidden="true">#</a> \u8BBE\u8BA1\u6A21\u5F0F</h1><p>\u8BBE\u8BA1\u539F\u5219\uFF1A\u9AD8\u5185\u805A\uFF0C\u4F4E\u8026\u5408</p><p>\u8BBE\u8BA1\u6A21\u5F0F(Design Pattern\uFF09\u662F\u524D\u8F88\u4EEC\u5BF9\u4EE3\u7801\u5F00\u53D1\u7ECF\u9A8C\u7684\u603B\u7ED3\uFF0C\u662F\u89E3\u51B3\u7279\u5B9A\u95EE\u9898\u7684\u4E00\u7CFB\u5217\u5957\u8DEF</p><p>GoF23</p><p>\u5B83\u4E0D\u662F\u8BED\u6CD5\u89C4\u5B9A\uFF0C\u800C\u662F\u4E00\u5957\u7528\u6765\u63D0\u9AD8\u4EE3\u7801\u53EF\u590D\u7528\u6027\u3001\u53EF\u7EF4\u62A4\u6027\u3001\u53EF\u8BFB\u6027\u3001\u7A33\u5065\u6027\u4EE5\u53CA\u5B89\u5168\u6027\u7684\u89E3\u51B3\u65B9\u6848\u3002</p><h2 id="\u5355\u4F8B\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u5355\u4F8B\u6A21\u5F0F" aria-hidden="true">#</a> \u5355\u4F8B\u6A21\u5F0F</h2><p>\u5F53\u5728\u9AD8\u5E76\u53D1\u7684\u60C5\u51B5\u4E0B\uFF0C\u5047\u8BBE\u4E00\u79D2\u949Fmain\u65B9\u6CD5\u6267\u884C\u4E86\u4E0A\u5343\u6B21/\u4E0A\u4E07\u6B21user\u5BF9\u8C61\u53CD\u590D\u521B\u5EFA\uFF0C\u5BF9\u8D44\u6E90\u5360\u7528\u4E25\u91CD</p><blockquote><p>\u8FD9\u5C31\u9700\u8981\u6211\u4EEC\u4F7F\u7528\u5355\u4F8B\u6A21\u5F0F\uFF1A\u53EA\u521B\u5EFA\u4E00\u4E2A\u5BF9\u8C61</p></blockquote><p>\u521B\u5EFA\u5F62\u5F0F\uFF1A\u997F\u6C49\u5F0F\uFF0C\u61D2\u6C49\u5F0F</p><h3 id="\u997F\u6C49\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u997F\u6C49\u5F0F" aria-hidden="true">#</a> \u997F\u6C49\u5F0F</h3><p>\u7C7B\u52A0\u8F7D\u65F6\u521B\u5EFA</p><p>\u521B\u5EFA\u7684\u65F6\u673A\u4E0D\u4E00\u6837</p><ul><li><p>\u79C1\u6709\u5316\u7684\u9759\u6001\u5C5E\u6027</p></li><li><p>\u79C1\u6709\u5316\u6784\u9020\u65B9\u6CD5\uFF08\u5728\u522B\u7684\u7C7B\u4E2D\u4E0D\u53EF\u4EE5\u521B\u5EFA\u5BF9\u8C61\uFF09</p></li><li><p>\u63D0\u4F9B\u4E00\u4E2A\u516C\u5F00\u7684\u9759\u6001\u7684\u65B9\u6CD5\uFF08\u8FD4\u56DE1\u6B65\u9AA4\u4E2D\u7684\u5BF9\u8C61\u5C5E\u6027\uFF09</p></li></ul><p>\u5728\u5176\u4ED6\u7C7B\u4E2D\u521B\u5EFA\u5BF9\u8C61\u65F6\u4E0D\u53EF\u4EE5new</p><h3 id="\u61D2\u6C49\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u61D2\u6C49\u5F0F" aria-hidden="true">#</a> \u61D2\u6C49\u5F0F</h3><p>\u7EBF\u7A0B\u4E0D\u5B89\u5168</p><ul><li>\u79C1\u6709\u5316\u9759\u6001\u5C5E\u6027</li><li>\u79C1\u6709\u5316\u6784\u9020\u65B9\u6CD5\uFF08\u518D\u522B\u7684\u7C7B\u4E2D\u65E0\u6CD5\u521B\u5EFA\u5BF9\u8C61\uFF09</li><li>\u63D0\u4F9B\u516C\u5F00\u7684\u9759\u6001\u7684\u65B9\u6CD5\uFF08\u8FD4\u56DE1\u4E2D\u5BF9\u8C61\u5C5E\u6027\uFF09</li></ul><p>\u8FD4\u56DE\u5BF9\u8C61\u7684\u65B9\u6CD5\u5728\u7B2C\u4E00\u6B21\u88AB\u8C03\u7528\u65F6\u624D\u4F1A\u521B\u5EFA\u5BF9\u8C61\uFF0C\u540E\u7EED\u76F4\u63A5\u8FD4\u56DE\u7B2C\u4E00\u6B21\u8C03\u7528\u7684\u5BF9\u8C61</p><p>\u8C03\u7528\u65F6\u521B\u5EFA</p><p>\u5347\u7EA7\u7248\u7EBF\u7A0B\u5B89\u5168\u7684\u61D2\u6C49\u5F0F synchronized\uFF0C\u76F8\u5BF9\u4E8E\u964D\u4F4E\u6548\u7387</p><p>\u53CC\u68C0\u9501double-check</p><p>\u997F\u6C49\u5F0F\uFF0C\u61D2\u6C49\u5F0F\uFF08\u7EBF\u7A0B\u5B89\u5168\u7684\uFF0C\u7EBF\u7A0B\u4E0D\u5B89\u5168\uFF0C\u53CC\u91CD\u6821\u9A8C\u2014double check\uFF09</p><h2 id="\u7B80\u5355\u5DE5\u5382\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u7B80\u5355\u5DE5\u5382\u6A21\u5F0F" aria-hidden="true">#</a> \u7B80\u5355\u5DE5\u5382\u6A21\u5F0F</h2><p>\u7528\u9014:\u4E3B\u8981\u89E3\u51B3\u63A5\u53E3\u9009\u62E9\u7684\u95EE\u9898\u3002\u5728\u4E0D\u540C\u60C5\u51B5\u4E0B\u521B\u5EFA\u4E0D\u540C\u7684\u5B9E\u73B0\u7C7B\u5BF9\u8C61</p><p>\u6B65\u9AA4</p><ul><li>\u4E00\u4E2A\u63A5\u53E3\u591A\u4E2A\u5B9E\u73B0</li><li>\u521B\u5EFA\u4E00\u4E2A\u5DE5\u5382\u7C7B\uFF0C\u5DE5\u5382\u7C7B\u4E2D\u5305\u542B\u516C\u5F00\u7684\u9759\u6001\u7684\u65B9\u6CD5\uFF08\u8FD4\u56DE\u503C\u63A5\u53E3\u7C7B\u578B\uFF0C\u65B9\u6CD5\u5305\u542B\u53C2\u6570:\u6839\u636E\u4E0D\u540C\u7684\u60C5\u51B5\uFF09</li><li>\u65B9\u6CD5\u4F53\u4E2D\u5206\u6790\u4E0D\u540C\u7684\u60C5\u51B5\uFF0C\u521B\u5EFA\u4E0D\u540C\u7684\u63A5\u53E3\u5B9E\u73B0\u7C7B\u5BF9\u8C61</li><li>\u5C06\u521B\u5EFA\u7684\u5B9E\u73B0\u7C7B\u5BF9\u8C61\u8FD4\u56DE\u51FA\u53BB</li></ul><h2 id="\u62BD\u8C61\u5DE5\u5382\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u62BD\u8C61\u5DE5\u5382\u6A21\u5F0F" aria-hidden="true">#</a> \u62BD\u8C61\u5DE5\u5382\u6A21\u5F0F</h2><p>\u4E00\u4E2A\u5DE5\u5382\u5DF2\u7ECF\u89E3\u51B3\u4E0D\u4E86\u95EE\u9898</p><p>\u7406\u89E3\uFF1A\u5DE5\u5382\u7684\u5DE5\u5382 \uFF0C\u5982\u4F55\u9009\u62E9\u5DE5\u5382</p><h2 id="\u89C2\u5BDF\u8005\u6A21\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u89C2\u5BDF\u8005\u6A21\u5F0F" aria-hidden="true">#</a> \u89C2\u5BDF\u8005\u6A21\u5F0F</h2><p>\u89E3\u51B3\u7684\u95EE\u9898\uFF1A\u5F53\u5BF9\u8C61\u5B58\u5728\u4E00\u5BF9\u591A\u65F6\uFF0C\u4E00\u7684\u5BF9\u8C61\u53D1\u751F\u6539\u53D8\u65F6\uFF0C\u6240\u6709\u4F9D\u8D56\u8FD9\u4E2A\u5BF9\u8C61\u7684\u5176\u4ED6\u5BF9\u8C61\u90FD\u4F1A\u5F97\u5230\u901A\u77E5</p><p>\u73ED\u7EA7dept(\u4E00\uFF09\u5BF9\u5E94\u5B66\u751Fstudent\uFF08\u591A)\u73ED\u7EA7\u5730\u5740\u6539\u4E86;\u5B66\u751F\u90FD\u5E94\u8BE5\u5F97\u5230\u901A\u77E5;</p><p>\u89C2\u5BDF\u8005\u6A21\u5F0F:\u5B9A\u4E49\u5BF9\u8C61\u4E4B\u95F4\u7684\u4E00\u79CD\u4E00\u5BF9\u591A\u4F9D\u8D56\u5173\u7CFB\uFF0C\u4F7F\u5F97\u6BCF\u5F53\u4E00\u4E2A\u5BF9\u8C61\u72B6\u6001\u53D1\u751F\u6539\u53D8\u65F6\uFF0C\u5176\u76F8\u5173\u4F9D\u8D56\u5BF9\u8C61\u90FD\u5F97\u5230\u901A\u77E5\u5E76\u88AB\u81EA\u52A8\u66F4\u65B0\u3002</p><p>\u522B\u540D</p><ul><li>\u53D1\u5E03-\u8BA2\u9605(Publish/Subscribe)\u6A21\u5F0F</li><li>\u6A21\u578B-\u89C6\u56FE(Model/View)\u6A21\u5F0F</li><li>\u6E90-\u76D1\u542C\u5668(Source/Listener)\u6A21\u5F0F</li><li>\u4ECE\u5C5E\u8005(Dependents)\u6A21\u5F0F</li></ul><hr><p><strong>\u5F00\u95ED\u539F\u5219</strong>:\u5BF9\u6269\u5C55\u5F00\u653E\uFF0C\u5BF9\u4FEE\u6539\u5173\u95ED</p><p><strong>\u91CC\u6C0F\u66FF\u6362\u539F\u5219</strong>:\u7EE7\u627F\u5FC5\u987B\u786E\u4FDD\u8D85\u7C7B\u6240\u62E5\u6709\u7684\u6027\u8D28\u5728\u5B50\u7C7B\u4E2D\u4ECD\u7136\u6210\u7ACB</p><p><strong>\u4F9D\u8D56\u5012\u7F6E\u539F\u5219</strong>:\u8981\u9762\u5411\u63A5\u53E3\u7F16\u7A0B\uFF0C\u4E0D\u8981\u9762\u5411\u5B9E\u73B0\u7F16\u7A0B\u3002</p><p><strong>\u5355\u4E00\u804C\u8D23\u539F\u5219</strong>:\u63A7\u5236\u7C7B\u7684\u7C92\u5EA6\u5927\u5C0F\u3001\u5C06\u5BF9\u8C61\u89E3\u8026\u3001\u63D0\u9AD8\u5176\u5185\u805A\u6027\u3002</p><p><strong>\u63A5\u53E3\u9694\u79BB\u539F\u5219</strong>:\u8981\u4E3A\u5404\u4E2A\u7C7B\u5EFA\u7ACB\u5B83\u4EEC\u9700\u8981\u7684\u4E13\u7528\u63A5\u53E3</p><p><strong>\u8FEA\u7C73\u7279\u6CD5\u5219</strong>:\u53EA\u4E0E\u4F60\u7684\u76F4\u63A5\u670B\u53CB\u4EA4\u8C08\uFF0C\u4E0D\u8DDF\u201C\u964C\u751F\u4EBA\u201D\u8BF4\u8BDD\u3002</p><p><strong>\u5408\u6210\u590D\u7528\u539F\u5219</strong>:\u5C3D\u91CF\u5148\u4F7F\u7528\u7EC4\u5408\u6216\u8005\u805A\u5408\u7B49\u5173\u8054\u5173\u7CFB\u6765\u5B9E\u73B0\uFF0C\u5176\u6B21\u624D\u8003\u8651\u4F7F\u7528\u7EE7\u627F\u5173\u7CFB\u6765\u5B9E\u73B0\u3002</p>',43);function a(n,l){return r}var h=e(i,[["render",a]]);export{h as default};
